import "./App.css";
import { useState } from "react";

function App() {
  const [showText, setShowText] = useState(true);

  return (
    <div className="container">
      <button
        onClick={() => {
          setShowText(!showText);
        }}>
        Show/Hide
      </button>

      {showText && <h1>HELLO, MY NAME IS AHMED!</h1>}
    </div>
  );
}

export default App;
