import "./App.css";
import { useState } from "react";

function App() {
  const [showText, setShowText] = useState("darkblue");
  const handleClick = () => {
    setShowText(show === "darkblue" ? "gold" : "darkblue");
  };

  return (
    <div className="App">
      <button onClick={handleClick}> Show/Hide </button>
      <h1 style={{ color: showText }}> HELLO, MY NAME IS AHMED! </h1>
    </div>
  );
}

export default App;
